#以下内容是spring属性定义变量，依据spring内容特异化
#dubbo服务端口
dubbo.server.port = 20881
#zookeeper服务地址
zk.address=127.0.0.1:2181

7 启动
mvn exec:java -Dexec.mainClass="cn.tomsnail.snail.core.starter.AppMain"

